import axios from "axios"
import { useState, useEffect } from "react"
import './Home.css'
import Coin from "./Coin"
import { useNavigate } from "react-router-dom"



const Home = () => {
    const url = 'https://api.coinstats.app/public/v1/coins?skip=0&limit=100&currency=AUD'
    const [coins, setCoins] = useState([])
    const [searchTerm, setSearchTerm] = useState('')

    // fetch crypto data
    useEffect(() => {
        axios.get(url).then((response) => {
            setCoins(response.data.coins)

            }).catch((error) => {
            console.log(error)
            })
        }, [])

    // filter coins based on search criteria
        const filteredCoins = coins.filter(coin => {
            return coin.name.toLowerCase().includes(searchTerm.toLowerCase())
        })

        const navigate = useNavigate()
        const navigateToCoinDetails = coinId => {
            navigate(`/coin/${coinId}`)
        }

    return (
    <div>
        <div className="crypto-search">
            <input 
            type="text" 
            placeholder="Search a currency ..." 
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value.toLowerCase())}
            />
        </div>

        <div className="crypto-display">
             {/* Render the title row */}
             <div className="coin-header">
                    <h4>Rank</h4>
                    <h4>Name</h4>
                    <h4>Symbol</h4>
                    <h4>Icon</h4>
                    <h4>Price/AUD</h4>
                </div>

        {/* if filtered coins match searchTerm render if not render not found message */}
            { filteredCoins.length > 0 ? (
                filteredCoins.map( coin => (
                    <Coin 
                    key={coin.id} 
                    rank={coin.rank} 
                    name={coin.name} icon={coin.icon} price={coin.price} symbol={coin.symbol} className='coin-style'
                    onClick={() => navigateToCoinDetails(coin.id)}
                    />
                )  
                )) : (
                    <>
                        <p className="no-coins">No coins found matching your criteria !</p>
                        <button className="go-back" onClick={() => setSearchTerm('')}>Go Back</button>
                    </>
                )
            }
        </div>
    
    </div>
  )
}

export default Home
