import React from 'react';
import { useParams, useLocation } from 'react-router-dom';
import './CoinDetails.css'

const CoinDetails = () => {
 
  const location = useLocation()
  const coin = location.state

  return (
    <div className='coin-details-container'>
        <div className="coin-details-header">
            <h4>Name</h4>
            <h4>Volume</h4>
            <h4>Market Cap</h4>
            <h4>Price 1H</h4>
            <h4>Price 1D</h4>
            <h4>Price 1W</h4>
        </div>

        <div className="coin-details">
            <h2>{coin.name}</h2>
            <h4>{coin.price ? coin.price.toFixed(2) : 'N/A'}</h4>
            <h4>{coin.volume}</h4>
            <h4>{coin.marketCap}</h4>
            <h4 style={{ color: coin.priceChange1h >= 0 ? 'green' : 'red' }}>
                ${coin.priceChange1h}
            </h4>
            <h4 style={{ color: coin.priceChange1d >= 0 ? 'green' : 'red' }}>
                ${coin.priceChange1d}
            </h4>
            <h4 style={{ color: coin.priceChange1w >= 0 ? 'green' : 'red' }}>
                ${coin.priceChange1w}
            </h4>
        </div>
    
    </div>
  );
};

export default CoinDetails;
